SELECT fat.fat_fatura as fatura, c.CLI_RS CLIENTE, (SELECT MIN(condi.cdi_container) as cont from containersdi condi  	 left join didadosinternos ddi1 on (ddi1.di = di.di_codigo) 	LEFT JOIN DIFATURA DIF1 ON (DIF1.dif_di = di.di_codigo) 	left JOIN fatura fat1 on (fat1.fat_indice = dif.dif_fatura) 	where (FAT.FAT_ESTADO = 'A')	AND di.di_codigo = condi.cdi_di  )conteiner FROM declaracao_importacao di left join didadosinternos ddi on (ddi.di = di.di_codigo) LEFT JOIN DIFATURA DIF ON (DIF.dif_di = di.di_codigo) left JOIN fatura fat on (fat.fat_indice = dif.dif_fatura) LEFT JOIN CLIENTE c ON fat.FAT_CLIENTE = c.CLI_CODIGO where (FAT.FAT_ESTADO = 'A') ORDER BY 3 DESC;